<?php	require_once('config/connection.php');	require_once('includes/datetime.php');	$get_docid = $_GET['docid'];	$get_cat = $_GET['cat'];		//select table for comments puposes	if($get_cat == "Document") {		$qry_tb = "tb_doc_upload";		$field_id = "id";	}elseif($get_cat == "Event") {		$qry_tb = "tb_events";		$field_id = "event_id";	}elseif($get_cat == "Schedule") {		$qry_tb = "tb_schedule";		$field_id = "sched_id";	}		$sql = "Select * From tb_doc_comments Where doc_id='$get_docid' And category='$get_cat' Order By doc_comment_id DESC";	foreach($dbh->query($sql) as $result_comment_doc) {		echo "<div id='up-triangle-comments' style='margin-top: 10px; margin-left: 40px;'></div>";		echo "<div id='display-doc-comments' style='margin-top: 0px;'>";		//select user post comment		$sql = "Select * From tb_users Where userid='$result_comment_doc[user_id]'";		foreach($dbh->query($sql) as $result_commentUserPic) {			$comment_UserPic = $result_commentUserPic['photoupload'];			$comment_FName = $result_commentUserPic['firstname'];			$comment_LName = $result_commentUserPic['lastname'];		}			echo "<div>";				echo "<img src='uploads/$comment_UserPic' width='50px'> ";		echo  "<div style='margin-top: -45px; margin-left: 60px;'>";			echo $comment_FName;						//select title from uploaded documents			foreach($dbh->query("Select * From $qry_tb Where $field_id='$get_docid'") as $doc_commented_res) {				$title_doc_commented = $doc_commented_res['title'];			}				echo " ".$comment_LName." was comment <b>".$title_doc_commented."</b> document<br>";												$date1 = $result_comment_doc['datetime'];												$date2 = $current_datetime;																								$diff = abs(strtotime($date2) - strtotime($date1)); 												$years   = floor($diff / (365*60*60*24)); 												$months  = floor(($diff - $years * 365*60*60*24) / (30*60*60*24)); 												$days    = floor(($diff - $years * 365*60*60*24 - $months*30*60*60*24)/ (60*60*24));												$hours   = floor(($diff - $years * 365*60*60*24 - $months*30*60*60*24 - $days*60*60*24)/ (60*60)); 												$minutes  = floor(($diff - $years * 365*60*60*24 - $months*30*60*60*24 - $days*60*60*24 - $hours*60*60)/ 60); 												$seconds = floor(($diff - $years * 365*60*60*24 - $months*30*60*60*24 - $days*60*60*24 - $hours*60*60 - $minutes*60)); 																								if($years == 0 && $months == 0 && $days == 0 && $hours == 0 && $minutes == 0) {													echo $seconds." seconds ago.";												}elseif($years == 0 && $months == 0 && $days == 0 && $hours == 0) {													if($minutes == 1) {														if($seconds==0) {															echo "<tt>".$minutes." minute ago.</tt>";														}else{															if($seconds==1) {																echo "<tt>".$minutes." minute and ".$seconds." second ago.</tt>";															}else{																echo "<tt>".$minutes." minute and ".$seconds." seconds ago.</tt>";															}														}																											} else {														if($seconds==0) {															echo "<tt>".$minutes." minutes ago.</tt>";														}else{															if($seconds==1) {																echo "<tt>".$minutes." minutes and ".$seconds." second ago.</tt>";															}else{																echo "<tt>".$minutes." minutes and ".$seconds." seconds ago.</tt>";															}														}																											}																									}elseif($years == 0 && $months == 0 && $days == 0) {													if($hours == 1) {														if($minutes==0){															echo "<tt>".$hours." hour ago.</tt>";														}else{																if($minutes==1) {																echo "<tt>".$hours." hour and ".$minutes." minute ago.</tt>";															} else {																echo "<tt>".$hours." hour and ".$minutes." minutes ago.</tt>";															}														}													}else{														if($minutes==0){															echo "<tt>".$hours." hours ago.</tt>";														}else{																if($minutes==1) {																echo "<tt>".$hours." hours and ".$minutes." minute ago.</tt>";															} else {																echo "<tt>".$hours." hours and ".$minutes." minutes ago.</tt>";															}														}													}																									}elseif($years == 0 && $months == 0) {													if($days==1) {														if($hours==0) {															echo "<tt>".$days." day ago.</tt>";														}else{															if($hours==1) {																echo "<tt>".$days." day and ".$hours." hour ago.</tt>";															}else{																echo "<tt>".$days." day and ".$hours." hours ago.</tt>";															}														}													}else{														if($hours==0) {															echo "<tt>".$days." days ago.";														}else{															if($hours==1) {																echo "<tt>".$days." days and ".$hours." hour ago.</tt>";															}else{																echo "<tt>".$days." days and ".$hours." hours ago.</tt>";															}														}																											}																									}elseif($years == 0) {													if($months==1){														if($days==0){															echo "<tt>".$months." month ago.</tt>";														}else{															if($days==1) {																	echo "<tt>".$months." month and ".$days." day ago.</tt>";															}else{																echo "<tt>".$months." month and ".$days." days ago.</tt>";															}														}													}else{														if($days==0){															echo "<tt>".$months." months ago.</tt>";														}else{															if($days==1) {																	echo "<tt>".$months." months and ".$days." day ago.</tt>";															}else{																echo "<tt>".$months." months and ".$days." days ago.</tt>";															}														}													}																									}else{													if($years==1) {														if($months==0) {															echo "<tt>".$years." year ago.</tt>";														}else{															if($months==1) {																echo "<tt>".$years." year and ".$months." month ago.</tt>";															}else{																echo "<tt>".$years." year and ".$months." months ago.</tt>";															}														}																											}else{														if($months==0) {															echo "<tt>".$years." years ago.</tt>";														}else{															if($months==1) {																echo "<tt>".$years." years and ".$months." month ago.</tt>";															}else{																echo "<tt>".$years." years and ".$months." months ago.</tt>";															}														}													}												}												echo  "</div>";		echo "</div>";				echo "<div style='margin-top: 13px;'>";				echo $result_comment_doc['comment']."<br>";		echo "</div>";			echo "</div>";			}?>